import { Component } from "react";  
import MovieCard from "./MovieCard";
class MovieList extends Component{
    constructor(){
        super();
        this.state = {
            movies : [
                {
                  title: 'The Avengers',                  
                  plot:
                    "Earth's mightiest heroes must come together and learn to fight as a team if they are going to stop the mischievous Loki and his alien army from enslaving humanity.",
                  poster:
                    'https://m.media-amazon.com/images/M/MV5BNDYxNjQyMjAtNTdiOS00NGYwLWFmNTAtNThmYjU5ZGI2YTI1XkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_SX300.jpg',
                  rating: '8.0',            
                  price: 99,            
                  stars: 0,
                  fav: false,
                  isInCart: false                  
                },
                {
                  title: 'The Dark Knight',                  
                  plot:
                    'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham, Batman must accept one of the greatest psychological and physical tests of his ability to fight injustice.',
                  poster:
                    'https://m.media-amazon.com/images/M/MV5BMTMxNTMwODM0NF5BMl5BanBnXkFtZTcwODAyMTk2Mw@@._V1_SX300.jpg',                  
                  rating: '9.0',            
                  price: 199,            
                  stars: 0,            
                  fav: false,
                  isInCart: false                  
                },
                {
                  title: 'Iron Man',                 
                  plot:
                    'After being held captive in an Afghan cave, billionaire engineer Tony Stark creates a unique weaponized suit of armor to fight evil.',                  
                  poster:
                    'https://m.media-amazon.com/images/M/MV5BMTczNTI2ODUwOF5BMl5BanBnXkFtZTcwMTU0NTIzMw@@._V1_SX300.jpg',                  
                  rating: '7.9',            
                  price: 139,            
                  stars: 0,            
                  fav: false,
                  isInCart: false                  
                },
                {
                    title: 'Top Gun: Maverick',                 
                    plot:
                      'After being held captive in an Afghan cave, billionaire engineer Tony Stark creates a unique weaponized suit of armor to fight evil.',                  
                    poster:
                      'https://m.media-amazon.com/images/I/71BokibfVUL._AC_UL480_FMwebp_QL65_.jpg',                  
                    rating: '8.9',            
                    price: 239,            
                    stars: 0,            
                    fav: false,
                    isInCart: false                  
                  }
            ]
        }
    }

    handleIncStar = (movie) =>{
        const {movies}  = this.state;
        const mid = movies.indexOf(movie);
        if(movies[mid].stars >= 5){
            return;
        }
        movies[mid].stars += 0.5;
        
        this.setState({
            movies: movies
        })
    }
    handleDecStars = (movie) => {
        const {movies} = this.state;
           const mid = movies.indexOf(movie);
           if(movies[mid].stars <= 0){
            return ;
           }
           movies[mid].stars -= 0.5;

           this.setState({
            movies: movies
           })
           
    }

    handletoggleFav = (movie) => {
        const {movies} = this.state;
        const mid = movies.indexOf(movie);
        movies[mid].fav = !movies[mid].fav;
        this.setState({
            movies
        })
    }
    render(){
        // const {title, plot, price, rating, stars, fav, isInCart} = this.state;
        const {movies} = this.state;
        return(
            <>
            {/* <MovieCard movies = {this.state}
                        /> */}
            {movies.map((movie, index) => <MovieCard key = {index}
              movies = {movie} addStars={this.handleIncStar} decStars={this.handleDecStars} toggleFav = {this.handletoggleFav}/>)}
           
            
            </>
        )
    }
}

export default MovieList;
