import React from "react";

class MovieCard extends React.Component{
    constructor(){
        super();
        this.state = {
            title: "The Avengers!",
            plot: "Supernatural power shown in the movie",
            price: 199,
            rating: 8.9,
        }
        // Binding 2nd Method.
        // this.addStars = this.addStars.bind(this);
    }
    addStars(){
        console.log("this: ", this);
    }
    render(){
        const {title, plot, price, rating} = this.state;
        return (
            <div className="main">
                <div className="movie-card">
                    <div className="left">
                        <img alt="poster" src="https://m.media-amazon.com/images/I/71Rm8RgpemL._AC_UL480_FMwebp_QL65_.jpg" />
                    </div>
                    <div className="right">
                        <div className="title">{this.state.title}</div>
                        <div className="plot">{plot}</div>
                        <div className="price">Rs.{price}</div>
                        <div className="footer">
                            <div className="rating">{rating}</div>
                            <div className="star-dis">

                                <img alt="decrease" className="str-btn" src="https://cdn-icons-png.flaticon.com/128/1828/1828906.png"/>

                                <img alt="stars" src="https://cdn-icons-png.flaticon.com/128/1828/1828884.png" className="stars"/>

                                <img alt="increase" className="str-btn" src="https://cdn-icons-png.flaticon.com/128/992/992651.png" onClick={this.addStars.bind(this)} />
                            {/* 1st method of binding above.*/}

                                <span>0</span>
                            </div>
                            <button className="favourite-btn">Favourite</button>
                            <button className="cart-btn">Add to cart</button>
                        </div>
                    </div>

                </div>
            </div>
        )       
    }
}
export default MovieCard;
