import React from "react";

class MovieCard extends React.Component{
    constructor(){
        super();
        this.state = {
            title: "The Avengers!",
            plot: "Supernatural power shown in the movie",
            price: 199,
            rating: 8.9,
            stars: 0,
            fav : false,
            isInCart: false,
        }

        //Globally Binding. 
        // Binding 2nd Method.
        // this.addStars = this.addStars.bind(this);
    }

    reduceStars = () => {
        if(this.state.stars <= 0){
            return;
        }
        //form1
        this.setState({
            stars: this.state.stars - 0.5
        });
        
        
        // form 2
        // this.setState((prevState) => {
        //     return{
        //         stars: this.state.stars - 0.5
        //     }
        // });
    }
    // In Arrow function {binding} is not require.
    addStars = () => {
        // this.state.stars++; 
        // we need to re-render this again for occuring. but this is very complex task.so we use it {setState => re-render the component} method.
        // Form 1
        // this form is used when, like we want to change in title then this can be used directly.
        // please do not use when want to change in previous state.{not necessary}
        if(this.state.stars >= 5){
            return;
        }
        this.setState({
            stars: this.state.stars + 0.5}
        // }, () => 
        // console.log("stars call:",  this.state.stars)
        
        );
        // console.log("stars:",  this.state.stars);

        // 2nd Form
        // this form is used when we want to change in previous state.{not necessary just concept clearification.}
        // this take callback function like {previous state}
        
        // this.setState((prevState) => {
        //     return{
        //         stars: prevState.stars+0.5
        //     }
        // });
    }
    handleFev = () => {
        
        this.setState({
            fav: !this.state.fav
        });
    }
    render(){
        const {title, plot, price, rating, stars,fav, isInCart} = this.state;
        return (
             <div className="main">
                <div className="movie-card">
                    <div className="left">
                        <img alt="poster" src="https://m.media-amazon.com/images/I/71Rm8RgpemL._AC_UL480_FMwebp_QL65_.jpg" />
                    </div>
                    <div className="right">
                        <div className="title">{this.state.title}</div>
                        <div className="plot">{plot}</div>
                        <div className="price">Rs.{price}</div>
                        <div className="footer">
                            <div className="rating">{rating}</div>
                            <div className="star-dis">

                                <img alt="decrease" className="str-btn" src="https://cdn-icons-png.flaticon.com/128/1828/1828906.png" onClick={this.reduceStars}/>

                                <img alt="stars" src="https://cdn-icons-png.flaticon.com/128/1828/1828884.png" className="stars"/>

                                <img alt="increase" className="str-btn" src="https://cdn-icons-png.flaticon.com/128/992/992651.png" onClick={this.addStars} />
                            {/* 1st method of binding : 
                            onClick={this.addStars.bind(this)}
                            */}

                                <span>{stars}</span>
                            </div>
                            {/* {fav?<button className="unfavourite-btn" onClick={this.handleFev}>Un-favourite</button>:<button className="" onClick={this.handleFev}>Favourite</button>} */}

                            <button className={fav?"unfavourite-btn": "favourite-btn"} onClick={this.handleFev}> {fav?"Un-favourite":"Favourite"}</button>
                            
                            <button className={isInCart?"removecard-btn":"cart-btn"} onClick={this.toogleCart}>{isInCart?"":"Add to cart"}</button>

                        </div>
                    </div>

                </div>
            </div>
        )       
    }
}
export default MovieCard;
